'Y' 'N'
)

mapper.select:
SELECT A.CONVERSATION_ID as conversationId, A.APPROVE_ID as issueId, A.SUP_COM_REGNO as txtSupComRegNo, A.BYR_COM_REGNO as txtByrComRegNo, A.DTI_WDATE as txtDate, A.SUPBUY_TYPE as txtSupbuy_type, B.DIRECTION as txtDirection, B.DTI_STATUS as dtiStatus, B.RETURN_CODE as returnCode FROM XXSB_DTI_MAIN A INNER JOIN XXSB_DTI_STATUS B ON (A.SUPBUY_TYPE=#{txtSupbuy_type} AND B.SUPBUY_TYPE=#{txtSupbuy_type}
AND B.DTI_STATUS='I' AND B.DTI_STATUS='C'
AND A.CONVERSATION_ID=B.CONVERSATION_ID) ORDER BY A.DTI_WDATE DESC

select * from xxsb_dti_main
where conversation_id=#{conversationId}
and supbuy_type=#{}

        select * from xxsb_dti_status
        where conversation_id=#{conversationId}
		and supbuy_type=#{}

